mapper.update:
UPDATE
	YOMUL_MEMBERS
SET
	KAKAO_ID = #{kakao_id}
WHERE
	NO = #{no}

mapper.select:
SELECT HASHSALT
FROM YOMUL_MEMBERS
WHERE EMAIL = #{email}

SELECT NO, NICKNAME, AUTHORITY
from YOMUL_MEMBERS
where email = #{email} and pw = #{pw}

mapper.insert:
INSERT INTO YOMUL_MEMBERS
(
	  NO
	, EMAIL
	, PW
	, NICKNAME
	, HASHSALT
	, SUBSCRIBE
) 
VALUES
(
	  'M'||YOMUL_MEMBERS_NO_SEQ.NEXTVAL
	, #{email}
	, #{pw}
	, #{nickname}
	, #{hashsalt}
	, #{subscribe}
)

mapper.select:
SELECT COUNT(*) 
FROM YOMUL_TRADE_HISTORY 
WHERE BUYER = #{no}

SELECT COUNT(*) 
FROM YOMUL_TRADE_HISTORY
 WHERE SELLER = #{no}

SELECT COUNT(*) 
FROM YOMUL_FAVORITE_LISTS 
WHERE MEMBER_NO = #{no}
