mapper.select:
SELECT EMAIL, NICKNAME, PHONE, GENDER, INTRO, F.FILENAME FILENAME 
FROM YOMUL_MEMBERS M LEFT JOIN YOMUL_FILES F ON M.NO = F.ARTICLE_NO 
WHERE M.NO = #{no}

SELECT COUNT(*) 
FROM YOMUL_TRADE_HISTORY 
WHERE BUYER = #{no}

SELECT COUNT(*) 
FROM YOMUL_TRADE_HISTORY
 WHERE SELLER = #{no}

SELECT COUNT(*) 
FROM YOMUL_FAVORITE_LISTS 
WHERE MEMBER_NO = #{no}
