mapper.delete:
DELETE FROM
	YOMUL_NEAR_ARTICLES
WHERE
		NO = #{near.no}
	AND 'ADMIN' = #{member.authority}

mapper.insert:
INSERT INTO YOMUL_NEAR_ARTICLES
(
	  NO
	, WRITER
	, WRITER_NICKNAME
	, TITLE
	, CATEGORY
	, PRICE
	, HP
	, CONTENT
	, CHATCHECK
	, MAINFILE

)
VALUES
(
	  'n'||YOMUL_NEAR_ARTICLES_NO_SEQ.NEXTVAL
	, #{vo.writer}
	, #{vo.writer_nickname}
	, #{vo.title}
	, #{vo.category}
	, #{vo.price}
	, #{vo.hp}
	, #{vo.content}
	, #{vo.chatCheck}
	, #{url}
)

INSERT INTO YOMUL_FILES
(
	  ARTICLE_NO
	, NO
	, FILENAME
)
VALUES
(
	  #{saveFileName}
	, YOMUL_NEAR_ARTICLES_NO_SEQ.NEXTVAL
	, #{originFilename}
)

mapper.update:
update
	yomul_near_articles 
set
	  title = #{vo.title}
	, price = #{vo.price}
	, hp = #{vo.hp}
	, content = #{vo.content}
	, chatCheck = #{vo.chatCheck} 
where
	no = #{no}

mapper.select:
select no, writer, mimg, vno, vname, vimg, title, category, price, hp, content, ndate, chatcheck, hits, files, mainFile
from v_y_near_articles
where no = #{no}

mapper.update:
update yomul_near_articles
set hits = hits + 1
where no = #{no}
