mapper.insert:
INSERT INTO YOMUL_NOTICES
(
NO
, WRITER
, TITLE
, CONTENT
)
VALUES
(
'N'||YOMUL_NOTICES_NO_SEQ.NEXTVAL
,
#{writer}
, #{title}
, #{content}
)

mapper.update:
UPDATE YOMUL_NOTICES
SET
TITLE=#{vo.title}
, CONTENT=#{vo.content}

WHERE
NO=#{no}

mapper.select:
SELECT TITLE, CONTENT, NDATE, SUBSTR(NO,2) NO
FROM YOMUL_NOTICES
WHERE NO = #{no}
