mapper.update:
update yomul_vendors
set 
info = #{info},
tel = #{tel},
addr = #{addr}
where no = #{no}

mapper.select:
select no, owner, name, category, info, tel, addr, news, customers, reviews, img
from v_y_vendors
where no = #{no}

select no
from v_y_vendors
where owner = #{no}

select no, owner, name, category, info, tel, addr, news, customers, reviews, img
from v_y_vendors
where owner = #{no}
