mapper.insert:
MERGE INTO YOMUL_FILES 
	USING DUAL ON (ARTICLE_NO = #{article_no})
WHEN MATCHED THEN
	UPDATE SET
		FILENAME = #{filename}
	WHERE
		ARTICLE_NO = #{article_no}
WHEN NOT MATCHED THEN
	INSERT
	(
		  ARTICLE_NO
		, NO
		, FILENAME
	)
	VALUES
	(
		  #{article_no}
		, #{no}
		, #{filename}
	)
